"use strict";var Task=require("../models").Task;module.exports={index:function(n,e){Task.findAll().then(function(n){e.render("tasks/index",{tasks:n})})},show:function(n,e){Task.findByPk(n.params.id).then(function(n){e.render("tasks/show",{task:n})})},create:function(n,e){Task.create({descripcion:n.body.descripcion}).then(function(n){e.json(n)}).catch(function(n){console.log(n),e.json(n)})},update:function(e,s){Task.update({descripcion:e.body.descripcion},{where:{id:e.params.id}}).then(function(n){s.redirect("/tasks/"+e.params.id)})},new:function(n,e){e.render("tasks/new")}};